package com.ahmet.postphotos.UI.Main.Fragments.Post;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.Toolbar;import android.widget.ImageView;import com.ahmet.postphotos.R;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.database.DataSnapshot;import com.google.firebase.database.DatabaseError;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import com.google.firebase.database.ValueEventListener;import com.squareup.picasso.Picasso;public class ShowPostActivity extends AppCompatActivity {    private String mPostKey = null;    private ImageView mImageShowPost;    private Toolbar mToolBar;    private DatabaseReference mDatabaseReference;    private FirebaseAuth mAuth;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_show_post);        mDatabaseReference = FirebaseDatabase.getInstance().getReference().child("blog");        mAuth = FirebaseAuth.getInstance();        mPostKey = getIntent().getStringExtra("postId");        mToolBar = findViewById(R.id.toolbar_show_post);        setSupportActionBar(mToolBar);        getSupportActionBar().setTitle("Show Photo");        getSupportActionBar().setDisplayHomeAsUpEnabled(true);        mImageShowPost = findViewById(R.id.image_show_post);        mDatabaseReference.child(mPostKey)                .addValueEventListener(new ValueEventListener() {                    @Override                    public void onDataChange(DataSnapshot dataSnapshot) {                        String postImage = dataSnapshot.child("image").getValue().toString();                        Picasso.with(ShowPostActivity.this)                                .load(postImage)                                .placeholder(R.drawable.default_post)                                .into(mImageShowPost);                    }                    @Override                    public void onCancelled(DatabaseError databaseError) {                    }                });    }}